import _ from 'lodash';
import jsonTypicode from '../apis/jsonTypicode';

// export const fetchPostsAndUser = () => async (dispatch, getState) => {
// 	await dispatch(fetchPosts());
// 	const userIds = _.uniq(_.map(getState().posts, 'userId'));
// 	userIds.forEach(id => dispatch(fetchUser(id)));
// };

export const fetchPosts = () => async dispatch => {
	const response = await jsonTypicode.get('/posts');
	dispatch({ type: 'FETCH_POSTS', payload: response.data });
};

export const fetchUser = () => dispatch => _fetchUser(id, dispatch);
const _fetchUser = _.memoize(async (id, dispatch) => {
	const response = await jsonTypicode.get(`/users/${id}`);
	dispatch({ type: 'FETCH_USER', payload: response.data });
});
