import React from 'react';
import { Field, reduxForm } from 'redux-form';

class StreamCreate extends React.Component {
	renderErr() {}
	renderInput({ input, label, meta }) {
		// input is destructured from formprops object
		console.log(meta);
		return (
			<div className='form-group'>
				<label>{label}</label>
				<input {...input} className='form-control' autoComplete='off' />
				<div>{this.renderErr(meta)}</div>
			</div>
		);
	}
	onSubmit = formValues => {
		console.log(formValues);
		this.validate(formValues);
	};

	render() {
		return (
			<form
				className='container '
				onSubmit={this.props.handleSubmit(this.onSubmit)}>
				<Field
					name='title'
					label='Enter the title'
					component={this.renderInput}
				/>

				<Field
					name='description'
					label='Enter the description'
					component={this.renderInput}
				/>
				<button className='btn btn-primary'>Submit</button>
			</form>
		);
	}
}
const validate = formValues => {
	const errors = {};
	if (!formValues.title) {
		errors.title = 'You must  enter a title';
	}
	if (!formValues.description) {
		errors.description = 'You must enter the description of the stream';
	}

	return errors;
};
export default reduxForm({
	form: 'streamCreate',
	validate,
})(StreamCreate);
