import React from 'react';
import { Field, reduxForm } from 'redux-form';
import { connect } from 'react-redux';
import { createStream } from '../../src/actions';

class StreamCreate extends React.Component {
	renderErr({ touched, error }) {
		if (touched && error) {
			return <div className='alert alert-danger mt-3'>{error}</div>;
		} else {
			return null;
		}
	}
	renderInput = ({ input, label, meta }) => {
		// input is destructured from formprops object
		return (
			<div className='form-group'>
				<label>{label}</label>
				<input {...input} className='form-control' autoComplete='off' />
				<div>{this.renderErr(meta)}</div>
			</div>
		);
	};
	onSubmit = formValues => {};

	render() {
		return (
			<form
				className='container '
				onSubmit={this.props.handleSubmit(this.onSubmit)}>
				<Field
					name='title'
					label='Enter the title'
					component={this.renderInput}
				/>

				<Field
					name='description'
					label='Enter the description'
					component={this.renderInput}
				/>
				<button className='btn btn-primary'>Submit</button>
			</form>
		);
	}
}
const validate = formValues => {
	const errors = {};
	if (!formValues.title) {
		errors.title = 'You must  enter a title';
	}
	if (!formValues.description) {
		errors.description = 'You must enter the description of the stream';
	}

	return errors;
};
const formWrapped = reduxForm({
	form: 'streamCreate',
	validate,
})(StreamCreate);
export default connect(null, { createStream })(formWrapped);
